mapper.select:
SELECT USER_ID userId,
       USER_NAME userName,
       USER_AGE userAge,
       USER_SEX userSex,
       CREATE_TIME createTime,
       UPDATE_TIME updateTime
FROM USER_INFO


        and USER_ID = #{userId}


        and USER_NAME = #{userName}


        and USER_AGE = #{userAge}


        and USER_SEX = #{userSex}

mapper.update:
UPDATE USER_INFO


        USER_NAME = #{userName}


        USER_AGE = #{userAge}


        USER_SEX = #{userSex}


        CREATE_TIME = #{createTime}


        UPDATE_TIME = #{updateTime}


WHERE
USER_ID = #{userId}
